import React, { Component } from 'react';
import './cmp_calc.css';
import $ from 'jquery';

class CmpCalc extends Component {
	constructor(props) {
		super(props);
		this.state = { srv_resp: 0 };
	}

	callAddNumbers = () => {
		let self = this;
		$.post('add-numbers', {}, (d, s) => {
			console.log({ d, s });
			self.setState({ srv_resp: d['result'] });
		});
	};

	render() {
		return (
			<div className="p-3">
				<h1 className="title center">Add Numbers in Backend</h1>
				<p>Press the button to get the server result</p>
				<button onClick={this.callAddNumbers} className="btn btn-primary">
					CALL SERVER
				</button>
				<br />
				<span>{this.state.srv_resp}</span>
			</div>
		);
	}
}

export default CmpCalc;
